import { ModalProvider, useModal } from "blue-react"

function InsideComp() {
    // Use the `useModal` hook.
    const { tell, verify, ask } = useModal()

    return (
        <>
            <p>
                Allowed params for the functions <code>tell</code> and <code>verify</code>:<br />
                <code>
                    (text: string, options:{" "}
                    {`{ title?: string, icon?: ReactNode, switchPrimaryBtn?: boolean, acceptBtnText?: string, cancelBtnText?: string }`}
                    ?)
                </code>
            </p>
            <p>
                Allowed params for the function <code>ask</code>:<br />
                <code>
                    (text: string, options:{" "}
                    {`{ title?: string, icon?: ReactNode, switchPrimaryBtn?: boolean, inputType?: string, acceptBtnText?: string, cancelBtnText?: string }`}
                    ?)
                </code>
            </p>

            <div className="d-flex gap-2">
                <button
                    className="btn btn-outline-secondary"
                    onClick={() => {
                        tell("Hey, what's up")
                    }}
                >
                    tell
                </button>

                <button
                    className="btn btn-outline-secondary"
                    onClick={
                        // Make sure to make your function "async" when using "await"
                        async () => {
                            // Use "await" to wait until the user enters something
                            const answer = await ask("Please enter password", {
                                title: "Tell me the correct answer",
                                inputType: "password"
                            })
                            if (answer && answer.toString() === "password") {
                                tell("That's correct!")
                            } else {
                                tell("Wrong!")
                            }
                        }
                    }
                >
                    ask
                </button>

                <button
                    className="btn btn-outline-secondary"
                    onClick={async () => {
                        const answer = await verify("Are you sure?", {
                            title: "Say yes or no",
                            icon: (
                                <span>
                                    <svg
                                        xmlns="http://www.w3.org/2000/svg"
                                        width="1rem"
                                        height="1rem"
                                        fill="currentColor"
                                        className="bi bi-question-circle"
                                        viewBox="0 0 16 16"
